mapper.select:
SELECT 

FROM bit_monitor_log a

WHERE a.id = #{id}

SELECT 

FROM bit_monitor_log a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.use_id = #{useId}


		AND a.type_flag = #{typeFlag}


		AND a.status_flag = #{statusFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bit_monitor_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bit_monitor_log SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
