mapper.select:
SELECT 

FROM bit_interface a

WHERE a.id = #{id}

SELECT 

FROM bit_interface a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.platform = #{platform}


		AND a.type = #{type}


		AND a.code = #{code}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bit_interface a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bit_interface SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
