mapper.select:
SELECT 

FROM bit_monitor a

WHERE a.id = #{id}

SELECT 

FROM bit_monitor a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.open_time = #{openTime}


		AND a.close_time = #{closeTime}


		AND a.status_flag = #{statusFlag}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM bit_monitor a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE bit_monitor SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
